// src/App.js
import React, { useState } from 'react';
import './styles.css'; // Assuming you will add styles here
import DestinationCard from './components/DestinationCard';

function App() {
    const [destinations, setDestinations] = useState([]);

    const handleSubmit = async (event) => {
        event.preventDefault();
        const budget = event.target.budget.value;
        const period = event.target.period.value;
        const travelType = event.target.type.value;

        const response = await fetch('http://127.0.0.1:5000/', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ budget, period, type: travelType }),
        });

        const data = await response.json();
        setDestinations(data);
    };

    return (
        <div>
            <h1>Travel Advisor</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Budget:
                    <select name="budget">
                        <option value="low">Low</option>
                        <option value="medium">Medium</option>
                        <option value="high">High</option>
                    </select>
                </label>
                <label>
                    Travel Period:
                    <select name="period">
                        <option value="winter">Winter</option>
                        <option value="spring">Spring</option>
                        <option value="summer">Summer</option>
                        <option value="autumn">Autumn</option>
                    </select>
                </label>
                <label>
                    Destination Type:
                    <select name="type">
                        <option value="beach">Beach</option>
                        <option value="mountain">Mountain</option>
                        <option value="city">City</option>
                        <option value="nature">Nature</option>
                    </select>
                </label>
                <button type="submit">Find Destinations</button>
            </form>
            {destinations.length > 0 ? (
                <div>
                    <h2>Suggested Destinations:</h2>
                    {destinations.map((destination, index) => (
                        <DestinationCard key={index} destination={destination} />
                    ))}
                </div>
            ) : (
                <p>No destinations found. Please adjust your preferences.</p>
            )}
        </div>
    );
}

export default App;
